mapper.update:
UPDATE BOOKHUB_STOCKS
SET
	BOOK_STOCK = BOOK_STOCK - 1
WHERE
	BOOK_NO = #{bno}
	AND LIB_NO = #{lno}

UPDATE BOOKHUB_STOCKS
SET 
    BOOK_STOCK = BOOK_STOCK + 1
WHERE
    BOOK_NO = #{bno}
AND LIB_NO = #{lno}

mapper.select:
SELECT SUM(BOOK_STOCK)
FROM BOOKHUB_STOCKS
WHERE BOOK_NO = #{bno}
AND LIB_NO = #{lno}
